            uniqueStrings.append(str) 
    return uniqueStrings

def shift_word(word):
        shifted_letter = ""
        for letter in word:
            letter_idx = ord(letter) + 2
            if letter_idx > ord('z'):
                letter_idx -= 26
            shifted_letter += chr(letter_idx)
        return shifted_letter

